"use strict";var codeText=document.getElementById("code-text"),codeRender=document.getElementById("code-render"),newText="";document.getElementById("button").addEventListener("click",function(e){var t=codeText.value;0<t.trim().length&&formatCode(t),console.log(newText)});var formatCode=function(e){newText+=getSelector(e),newText+=getComas(e),newText+=getSemicolons(e),newText+=getwoPoints(e)},getSelector=function(e){return e.substring(0,e.indexOf("{")).replace(e,"<span class>".concat(e,"</span>"))},getComas=function(e){return e.replace(",",'<span class="coma">,</span>')},getSemicolons=function(e){return e.replace(";",'<span class="semicolon">;</span>')},getwoPoints=function(e){return e.replace(":",'<span class="two-points">:</span>')};